    session.execute("make %s -[%s]> %s" % (a, l, b))
    if session.message() is not None:
        raise RuntimeError("Unexpected session message")

def kill(session, a, l, b=None):
    if (b is None):
        session.execute("kill %s -[%s]> ()" % (a, l))
    else:
        session.execute("kill %s -[%s]> %s" % (a, l, b))
    if session.message() is not None:
        raise RuntimeError("Unexpected session message")

