        if isinstance(node, ast.NameConstant):
            return node.value
        if isinstance(node, ast.BinOp):
            left = self.visit(node.left)
            right = self.visit(node.right)
            op = type(node.op)
            if op == ast.Add:
                return left + right
            if op == ast.Sub:
                return left - right
            if op == ast.Mult:
                return left * right
            if op == ast.Div:
                return left / right
            if op == ast.FloorDiv:
                return left // right
            if op == ast.Mod:
                return left % right
            if op == ast.Pow:
                return left ** right
        if isinstance(node, ast.UnaryOp):
            operand = self.visit(node.operand)
            op = type(node.op)
            if op == ast.USub:
